package tp3;

public class Tp3Exo2 
{

	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub
   
		int n = 10, p = 4;   
		long q = 2111000222 ;   
		float x = 1.75f;
		 
		  System.out.println("n+q('long') = " + (n+q));
		  System.out.println("n+x('float') = " + (n+x));
		  System.out.println("n%p*q('long') = " + (n%p*q));
		  System.out.println("n%-p*n('long') = " + (n%-p*n));
		  System.out.println("-n%p*n('long') = " + (-n%p*n));
	}
}
	


